        // Visual feedback
        ['dragenter', 'dragover'].forEach(eventName => {
            this.dropZone.addEventListener(eventName, () => this.dropZone.classList.add('drag-active'), false);
        });

        ['dragleave', 'drop'].forEach(eventName => {
            this.dropZone.addEventListener(eventName, () => this.dropZone.classList.remove('drag-active'), false);
        });

        this.dropZone.addEventListener('drop', (e) => this.handleDrop(e), false);
        document.addEventListener('paste', (e) => this.handlePaste(e));

        // File Input Fallback
        const fileInput = document.createElement('input');
        fileInput.type = 'file';
        fileInput.accept = 'image/*';
        fileInput.style.display = 'none';
        fileInput.id = 'vision-file-input';
        document.body.appendChild(fileInput);

        const uploadButton = document.getElementById('image-upload-btn');
        if (uploadButton) {
            uploadButton.addEventListener('click', (e) => {
                e.preventDefault();
                fileInput.click();
            });
        }

        fileInput.addEventListener('change', (e) => {
            if (e.target.files && e.target.files[0]) this.handleFile(e.target.files[0]);
        });
    }

    handleDrop(e) {
        const dt = e.dataTransfer;
        const files = dt.files;
        if (files.length > 0) this.handleFile(files[0]);
    }

    handlePaste(e) {
        const items = e.clipboardData.items;
        for (let i = 0; i < items.length; i++) {
            if (items[i].type.indexOf('image') !== -1) {
                const blob = items[i].getAsFile();
                if (blob) {
                    this.handleFile(blob);
                    break;
                }
            }
        }
    }

    handleFile(file) {
        if (!file.type.match('image.*')) {
            console.error('VisionController: Only image files are supported');
            return;
        }
        const reader = new FileReader();
        reader.onload = (e) => {
            this.imageBase64 = e.target.result;
            this.showPreview(this.imageBase64, file.name);
        };
        reader.readAsDataURL(file);
    }

    showPreview(base64Url, fileName) {
        if (!this.previewContainer) return;
        this.previewContainer.innerHTML = '';
        this.previewContainer.style.display = 'block'; // Show container

        const previewDiv = document.createElement('div');
        previewDiv.className = 'image-preview';
        
        const img = document.createElement('img');
        img.src = base64Url;
        img.style.maxWidth = '100px';
        img.style.maxHeight = '100px';
        img.style.borderRadius = '4px';
        
        const removeBtn = document.createElement('button');
        removeBtn.textContent = 'âŒ';
        removeBtn.style.marginLeft = '10px';
        removeBtn.onclick = () => this.clear();
        
        previewDiv.appendChild(img);
        previewDiv.appendChild(removeBtn);
        this.previewContainer.appendChild(previewDiv);
    }

    clear() {
        this.imageBase64 = null;
        if (this.previewContainer) {
            this.previewContainer.innerHTML = '';
            this.previewContainer.style.display = 'none';
        }
        const fileInput = document.getElementById('vision-file-input');
        if (fileInput) fileInput.value = '';
    }

    getImage() {
        return this.imageBase64;
    }
}

import { pipeline, env } from 'https://cdn.jsdelivr.net/npm/@xenova/transformers@2.14.0';

// Configure for WASM
env.allowLocalModels = false;
env.useBrowserCache = true;

class WhisperWorker {
    constructor() {
        this.pipe = null;
    }

    async init() {
        if (!this.pipe) {
            console.log("[WhisperWorker] Loading model...");
            this.pipe = await pipeline('automatic-speech-recognition', 'Xenova/whisper-tiny.en', { 
                device: 'wasm'
            });
            console.log("[WhisperWorker] Model loaded.");
        }
    }

    async transcribe(audioData) {
        if (!this.pipe) await this.init();

        console.log("[WhisperWorker] Processing audio...");
        const result = await this.pipe(audioData, { 
            language: 'english',
            chunk_length_s: 30,
            stride_length_s: 5
        });

        return result.text.trim();
    }
}

const worker = new WhisperWorker();

self.onmessage = async (e) => {
    const { type, data, id } = e.data;

    try {
        if (type === 'init') {
            await worker.init();
            self.postMessage({ type: 'init_done', id });
        } 
        else if (type === 'transcribe') {
            const text = await worker.transcribe(data);
            self.postMessage({ type: 'transcribe_result', text, id });
        }
    } catch (err) {
        self.postMessage({ type: 'error', error: err.message, id });
    }
};
